import React, { Component } from "react";
import { Text, View } from "react-native";
import { Message } from "./types";

type Props = {
  message: Message,
};

class Bubble extends Component<Props> {
  componentDidMount = () => {};

  render() {
    const { message } = this.props;

    return (
      <View>
        <Text>{message?.text}</Text>
      </View>
    );
  }
}

export default Bubble;
